import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import ResumePage from './pages/ResumePage';
import ResumePreviewPage from './pages/ResumePreviewPage';
import { ResumeProvider } from './data/resumeDataContext';

function App() {
  return (
    <ResumeProvider>
      <Router>
        <div className="min-h-screen bg-gray-100 p-4">
          {/* Optional Navbar */}
          <nav className="mb-6 flex gap-4 justify-center">
            <Link to="/resume" className="text-blue-600 font-semibold hover:underline">
              üìù Resume Form
            </Link>
            <Link to="/resume/preview" className="text-blue-600 font-semibold hover:underline">
              üìÑ Preview & Download
            </Link>
          </nav>

          {/* Route-based page rendering */}
          <Routes>
            <Route path="/resume" element={<ResumePage />} />
            <Route path="/resume/preview" element={<ResumePreviewPage />} />
          </Routes>
        </div>
      </Router>
    </ResumeProvider>
  );
}

export default App;
