import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import ResumePage from './pages/ResumePage';
import ResumePreviewPage from './pages/ResumePreviewPage';
import CompanyExplorer from './components/CompanyExplorer';
import RoadmapPage from './pages/RoadmapPage';
import InterviewPage from './pages/InterviewPage';
import { ResumeProvider } from './data/resumeDataContext'; // ‚úÖ Import ResumeProvider

function App() {
  return (
    <ResumeProvider> {/* ‚úÖ Context Provider wraps the entire app */}
      <Router>
        <div className="min-h-screen flex flex-col bg-[#f5f8fb] text-gray-800 font-inter">
          {/* Navbar */}
          <header className="bg-[#1f2937] text-white sticky top-0 shadow-md z-50">
            <div className="max-w-7xl mx-auto px-4 py-4 flex justify-between items-center">
              <h1 className="text-lg md:text-xl font-bold tracking-wide">CareerLaunch</h1>
              <nav className="space-x-4 text-sm md:text-base">
                <Link to="/" className="hover:text-yellow-400 transition-colors">üè† Home</Link>
                <Link to="/resume" className="hover:text-yellow-400 transition-colors">üìù Resume</Link>
                <Link to="/roadmap" className="hover:text-yellow-400 transition-colors">üìö Roadmap</Link>
                <Link to="/interview" className="hover:text-yellow-400 transition-colors">üíº Interview</Link>
                <Link to="/companies" className="hover:text-yellow-400 transition-colors">üè¢ Companies</Link>
              </nav>
            </div>
          </header>

          {/* Page Content */}
          <main className="flex-grow max-w-7xl mx-auto p-4 md:p-8">
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/resume" element={<ResumePage />} />
              <Route path="/resume/preview" element={<ResumePreviewPage />} />
              <Route path="/roadmap" element={<RoadmapPage />} />
              <Route path="/interview" element={<InterviewPage />} />
              <Route path="/companies" element={<CompanyExplorer />} />
            </Routes>
          </main>

          {/* Footer */}
          <footer className="bg-gray-100 py-4 text-center text-sm text-gray-500">
            ¬© 2025 CareerLaunch. All rights reserved.
          </footer>
        </div>
      </Router>
    </ResumeProvider>
  );
}

function Home() {
  return (
    <div className="text-center mt-20 text-2xl font-semibold text-[#1f2937]">
      üöÄ Welcome to CareerLaunch ‚Äì Your Ultimate Placement Prep Guide!
    </div>
  );
}

export default App;
