import React, { useState } from 'react';
import companies from '../data/companies.json';

const CompanyExplorer = () => {
  const [inputCGPA, setInputCGPA] = useState('');
  const [inputBranch, setInputBranch] = useState('');

  const filtered = companies.filter((company) =>
    company.roles.some((role) =>
      (!inputCGPA || role.eligibility.cgpa <= parseFloat(inputCGPA)) &&
      (!inputBranch || role.eligibility.branches.includes(inputBranch))
    )
  );

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">üéØ Explore Companies & Roles</h2>

      {/* üéõÔ∏è Filters */}
      <div className="flex gap-4 mb-6">
        <div>
          <label className="block text-sm">Min CGPA</label>
          <input
            type="number"
            step="0.1"
            value={inputCGPA}
            onChange={(e) => setInputCGPA(e.target.value)}
            className="border p-2 rounded w-32"
          />
        </div>

        <div>
          <label className="block text-sm">Branch</label>
          <select
            value={inputBranch}
            onChange={(e) => setInputBranch(e.target.value)}
            className="border p-2 rounded"
          >
            <option value="">All</option>
            <option value="CSE">CSE</option>
            <option value="IT">IT</option>
            <option value="ECE">ECE</option>
            <option value="EEE">EEE</option>
          </select>
        </div>
      </div>

      {/* üìã Filtered Companies Display */}
      {filtered.map((company, idx) => (
        <div key={idx} className="bg-white shadow p-4 rounded mb-4">
          <h3 className="text-xl font-semibold">{company.company}</h3>
          <ul className="mt-2 list-disc list-inside">
            {company.roles
              .filter(
                (role) =>
                  (!inputCGPA || role.eligibility.cgpa <= parseFloat(inputCGPA)) &&
                  (!inputBranch || role.eligibility.branches.includes(inputBranch))
              )
              .map((role, index) => (
                <li key={index}>
                  <strong>{role.title}</strong> ‚Äì CGPA ‚â• {role.eligibility.cgpa} ‚Äì Branches: {role.eligibility.branches.join(', ')}
                  <br />
                  Skills: {role.skills.join(', ')}
                </li>
              ))}
          </ul>
        </div>
      ))}
    </div>
  );
};

export default CompanyExplorer;
